import React, { useEffect } from "react";
import store from "store";



export default class Logout extends React.Component {

  
    constructor(props) {
      super(props);
      const token = store.get("token");
    if (!token) {
      this.props.history.push("/login");
    } else {
        store.remove("token");
        this.props.history.push("/login");
    }
      
      
    }
  }
