import React from 'react';
import { Table, message , Button, Input, Menu, Tag, Space} from 'antd';
import Layout from '../Layout/Layout';
import { SearchOutlined } from '@ant-design/icons';

export default class Homepage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data : [],
      searchText: '',
      searchedColumn: '',
    };
    this.getAlllist();
  }
  
  handleMenuClick(data) {
    console.log(data);
  }

  columns = [
    {
      title: 'UserName',
      dataIndex: 'username',
      key: 'username',
      ...this.getColumnSearchProps('username'),
    },
    {
        title: 'BookID',
        dataIndex: 'BookID',
        key: 'BookID',
    },
    {
        title: 'BookName',
        dataIndex: 'Name',
        key: 'Name',
    },
    {
        title: 'Stock',
        dataIndex: 'stock',
        key: 'stock',
    },
    {
      title: 'StartDay',
      dataIndex: 'startday',
      key: 'startday',
    },
    {
      title: 'EndDay',
      dataIndex: 'endday',
      key: 'endday',
    },
    {
      title: 'Status',
      key: 'status',
      dataIndex: 'status',
      render: status => (
        status == 0 ? 
          <Tag color={"blue"} key={status}>
            ยังไม่ได้มารับ
          </Tag> 
        :
        status == 1 ? 
          <Tag color={"volcano"} key={status}>
            มารับแล้ว
          </Tag> 
        :
          <Tag color={"green"} key={status}>
            คืนเรียบร้อย
          </Tag> 
      ),
    },
    {
      title: 'Action',
      render: (data) => (
        <Menu style={{width:90}}>
          <Menu.Item key="1" onClick={ ()=> this.handleMenuClick(data)}>
            มารับแล้ว
          </Menu.Item>
          <Menu.Item key="2">
            มาคืนแล้ว
          </Menu.Item>
        </Menu>
      ),
    },
  ];

  getAlllist = async() => {
    var data = [];
    const requestOptions = {
      method: 'GET',
      headers: { 
          'Content-Type': 'application/json',
      },
    };
    await fetch('/getAllList', requestOptions)
      .then(response=> response.json())
      .then(myJson=> {
        myJson.forEach(element => {
          console.log(element);
          data.push(element);
        });
        this.setState({data:data})
        console.log(this.state.data);
      }) 
  };

  deleteBook = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/Delete?id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            if(myJson.status == 'Pass')
            {
                message.info('ลบหนังสือสำเร็จ');
                this.getAllbook();
            }
    }) 
  }

  getColumnSearchProps = dataIndex => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={node => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
          style={{ width: 188, marginBottom: 8, display: 'block' }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            Search
          </Button>
          <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
            Reset
          </Button>
        </Space>
      </div>
    ),
    filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase())
        : '',
    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
  });

  render() {
    return (
        <Layout>
            <Table columns={this.columns} dataSource={this.state.data} />
        </Layout>
      
    );
  }
}
