import React from 'react';
import { Table, message , Button, Dropdown, Menu, Tag} from 'antd';
import Layout from '../Layout/Layout';

export default class Homepage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data : []
    };
    this.getAlllist();
  }
  
  handleMenuClick(data) {
    console.log(data);
  }

  columns = [
    {
        title: 'No.',
        dataIndex: 'BookID',
        key: 'BookID',
    },
    {
      title: 'Name',
      dataIndex: 'Name',
      key: 'Name',
    },
    {
        title: 'Author',
        dataIndex: 'Author',
        key: 'Author',
    },
    {
        title: 'Year',
        dataIndex: 'Year',
        key: 'Year',
    },
    {
        title: 'Stock',
        dataIndex: 'stock',
        key: 'stock',
    },
    {
      title: 'Status',
      key: 'status',
      dataIndex: 'status',
      render: status => (
        status == 0 ? 
          <Tag color={"blue"} key={status}>
            ยังไม่ได้มารับ
          </Tag> 
        :
        status == 1 ? 
          <Tag color={"volcano"} key={status}>
            มารับแล้ว
          </Tag> 
        :
          <Tag color={"green"} key={status}>
            คืนเรียบร้อย
          </Tag> 
      ),
    },
    {
      title: 'Action',
      render: (data) => (
        <Menu>
          <Menu.Item key="1" onClick={ ()=> this.handleMenuClick(data)}>
            มารับแล้ว
          </Menu.Item>
          <Menu.Item key="2">
            มาคืนแล้ว
          </Menu.Item>
        </Menu>
      ),
    },
  ];

  getAlllist = async() => {
    var data = [];
    const requestOptions = {
      method: 'GET',
      headers: { 
          'Content-Type': 'application/json',
      },
    };
    await fetch('/getAllList', requestOptions)
      .then(response=> response.json())
      .then(myJson=> {
        myJson.forEach(element => {
          console.log(element);
          data.push(element);
        });
        this.setState({data:data})
        console.log(this.state.data);
      }) 
  };

  deleteBook = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/Delete?id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            if(myJson.status == 'Pass')
            {
                message.info('ลบหนังสือสำเร็จ');
                this.getAllbook();
            }
    }) 
  }

  render() {
    return (
        <Layout>
            <Table columns={this.columns} dataSource={this.state.data} />
        </Layout>
      
    );
  }
}
