import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import store from "store";
import Layout from '../Layout/Layout';
import { Form, Button, Space, Input } from 'antd';
export default class AddBook extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      author: '',
      year:'',
      url: '',
      stock : 0
    };
  }
  
  formRef = React.createRef();
  
  layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 14,
    },
  };

  tailLayout = {
    wrapperCol: {
      offset: 5,
      span: 16,
    },
  };

  tailLayout1 = {
    wrapperCol: {
      offset: 6,
      span: 16,
    },
  };

  render() {
    return (
      <Layout>
        <Form {...this.layout} name="basic" ref={this.formRef} style={{marginRight:'10%'}} >
          <Form.Item {...this.tailLayout1}>
            <label style={{ color: 'black',fontWeight:'bold'}}>Add Book</label>
          </Form.Item>
          
          <Form.Item label={<label style={{ color: 'black',fontWeight:'bold' }}>Name</label>} name="username" rules={[{ required: true, message: 'Please input your username!' },]} >
            <Input id = 'input1' onChange={this.onChangeUser}/>
          </Form.Item>

          <Form.Item  label={<label style={{ color: 'black',fontWeight:'bold' }}>Author</label>}  name="password" rules={[{ required: true, message: 'Please input your password!' }]} >
            <Input id = 'input2' onChange={this.onChangePass}/>
          </Form.Item>

          <Form.Item  label={<label style={{ color: 'black',fontWeight:'bold' }}>Year</label>}  name="Email" rules={[{ required: true, message: 'Please input your email!' }]} >
            <Input id = 'input3' onChange={this.onChangeEmail}/>
          </Form.Item>

          <Form.Item  label={<label style={{ color: 'black',fontWeight:'bold' }}>URL</label>}  name="ID" rules={[{ required: true, message: 'Please input your email!' }]} >
            <Input id = 'input4' onChange={this.onChangeID}/>
          </Form.Item>

          <Form.Item  label={<label style={{ color: 'black',fontWeight:'bold' }}>stock</label>}  name="ID" rules={[{ required: true, message: 'Please input your email!' }]} >
            <Input id = 'input5' onChange={this.onChangeID}/>
          </Form.Item>

          <Form.Item {...this.tailLayout1}>
            <div>
              <Button onClick = {() => this.onClick()} style = {{ width: '10vh'}} type="primary" >
                Submit
              </Button>
              &nbsp;&nbsp;&nbsp;
              <Button style = {{ width: '10vh'}} type="primary" onClick={() => this.onReset()} >
                Reset
              </Button>
            </div>
          </Form.Item>

        </Form>
      </Layout>
      
    );
  }
}
