import React from 'react';
import { Table, message , Button, Dropdown, Menu} from 'antd';
import Layout from '../Layout/Layout';

export default class Homepage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data : []
    };
    this.getAllbook();
  }
  
  menu = (
    <Menu onClick={this.handleMenuClick()}>
      <Menu.Item key="1" >
        1st menu item
      </Menu.Item>
      <Menu.Item key="2">
        2nd menu item
      </Menu.Item>
      <Menu.Item key="3">
        3rd menu item
      </Menu.Item>
    </Menu>
  );

  handleButtonClick(e) {
    message.info('Click on left button.');
    console.log('click left button', e);
  }
  
  handleMenuClick(e) {
    message.info('Click on menu item.');
    console.log('click', e);
  }

  columns = [
    {
        title: 'No.',
        dataIndex: 'BookID',
        key: 'BookID',
      },
    {
      title: 'Name',
      dataIndex: 'Name',
      key: 'Name',
    },
    {
        title: 'Author',
        dataIndex: 'Author',
        key: 'Author',
    },
    {
        title: 'Year',
        dataIndex: 'Year',
        key: 'Year',
    },
    {
        title: 'Stock',
        dataIndex: 'stock',
        key: 'stock',
    },
    {
      title: 'Action',
      render: (data) => (
        <Dropdown overlay={() => this.menu}>
          <Button>
            Button 
          </Button>
        </Dropdown>
      ),
    },
  ];

  getAllbook = async() => {
    var data = [];
    const requestOptions = {
      method: 'GET',
      headers: { 
          'Content-Type': 'application/json',
      },
    };
    await fetch('/allbook', requestOptions)
      .then(response=> response.json())
      .then(myJson=> {
        myJson.forEach(element => {
          console.log(element);
          data.push(element);
        });
        this.setState({data:data})
      }) 
  };

  deleteBook = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/Delete?id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            if(myJson.status == 'Pass')
            {
                message.info('ลบหนังสือสำเร็จ');
                this.getAllbook();
            }
    }) 
  }

  render() {
    return (
        <Layout>
            <Table columns={this.columns} dataSource={this.state.data} />
        </Layout>
      
    );
  }
}
