import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import store from "store";
import Layout from '../Layout/Layout';
import { Table, message , Button} from 'antd';
export default class MyList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        data : []
    }
    this.getList();
  }

  getList = async() =>{
    var arr = [];
    const requestOptions = {
        method: 'GET',
        headers: { 
            'Content-Type': 'application/json',
        },
      };
      await fetch('/getList?user='+store.get('token'), requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            myJson.forEach(element => {
                arr.push(element);
            });
            this.setState({data:arr});
            console.log(this.state.data);
        }) 
  }

  removeList = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/removeList?user='+store.get('token')+'&id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            if(myJson.status == 'Pass')
            {
                message.info('ลบหนังสือเล่มนี้จากรายงานสำเร็จ');
                this.getList();
            }
    }) 
  }

  

  columns = [
    {
      title: 'Name',
      dataIndex: 'Name',
      key: 'Name',
    },
    {
        title: 'Author',
        dataIndex: 'Author',
        key: 'Author',
    },
    {
        title: 'Year',
        dataIndex: 'Year',
        key: 'Year',
    },
    {
      title: 'Action',
      render: (data) => (
        <Button style = {{ width: '10vh'}} type="primary" onClick={() => this.removeList(data.BookID)} danger >
            Delete
        </Button>
      ),
    },
  ];

  render() {
    return (
      <Layout>
          <Table columns={this.columns} dataSource={this.state.data} />
          <Button style = {{ alignItems:'flex-end',justifyContent:'flex-end',width: '10vh'}} type="primary" >
            Delete
        </Button>
      </Layout>
      
    );
  }
}
