import React from 'react';
import 'antd/dist/antd.css';
import { Form, Input, Button, message } from 'antd';
import './custom.css'
import Layout from '../Layout/Layout';
import store from "store";

export default class ChangePassword extends React.Component {

  
  constructor(props) {
    super(props);
    this.state = {
      pass: '',
      newpass: '',
      confirmpass: '',
    };
  }

  
  formRef = React.createRef();

   layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };

  tailLayout = {
    wrapperCol: {
      offset: 5,
      span: 16,
    },
  };

  tailLayout1 = {
    wrapperCol: {
      offset: 6,
      span: 16,
    },
  };
  
  onClick =  () =>  {
    this.fetchUser();
    //console.log(this.state.status);
  };

  onReset =  () => {
    this.setState({user:''});
    this.setState({pass:''});
    this.formRef.current.resetFields();
  };

  onChangeUser = (e) => {
    this.setState({user:e.target.value})
  };

  onChangePass = (e) => {
    this.setState({pass:e.target.value})
  };

  render() {
    return (
    <Layout>
        <div class='backgrond2' style={{backgroundColor:'rgba(52, 52, 52, 0.8)'}}>
          <Form {...this.layout} name="basic" ref={this.formRef} >
          <Form.Item {...this.tailLayout1}>
            <label style={{ color: 'white',fontWeight:'bold'}}>Change You Password</label>
          </Form.Item>
          
          <Form.Item label={<label style={{ color: 'white',fontWeight:'bold',margin:'10%'}}>Current Password</label>} rules={[{ required: true, message: 'Please input your Current Password!' }]} >
            <Input style={{marginLeft:'10%'}} id = 'input1' onChange={this.onChangeUser}/>
          </Form.Item>

          <Form.Item  label={<label style={{ color: 'white',fontWeight:'bold' }}>New Password</label>} rules={[{ required: true, message: 'Please input your New password!' }]} >
            <Input.Password style={{marginLeft:'10%'}} id = 'input2' onChange={this.onChangePass}/>
          </Form.Item>

          <Form.Item  label={<label style={{ color: 'white',fontWeight:'bold',margin:'10%'}}>Confirm Password</label>} rules={[{ required: true, message: 'Please input your Confirm password!' }]} >
            <Input.Password style={{marginLeft:'10%'}} id = 'input2' onChange={this.onChangePass}/>
          </Form.Item>

          <Form.Item {...this.tailLayout1}>
            <div>
              <Button onClick = {() => this.onClick()} style = {{ width: '10vh'}} type="primary" >
                Submit
              </Button>
              &nbsp;&nbsp;&nbsp;
              <Button style = {{ width: '10vh'}} type="primary" onClick={() => this.onReset()} >
                Reset
              </Button>
            </div>
          </Form.Item>          
        </Form>
        </div>
    </Layout>
    );
  }
}
