import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import store from "store";
import Layout from '../Layout/Layout';
import { Table, message , Button} from 'antd';
export default class MyList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        data : []
    }
    this.getBookmard();
  }

  getBookmard = async() =>{
    var arr = [];
    const requestOptions = {
        method: 'GET',
        headers: { 
            'Content-Type': 'application/json',
        },
      };
      await fetch('/getBookmard?user='+store.get('token'), requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            myJson.forEach(element => {
                arr.push(element);
            });
            this.setState({data:arr});
        }) 
  }

  removeBookmark = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/removeBookmark?user='+store.get('token')+'&id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            if(myJson.status == 'Pass')
            {
                message.info('ลบหนังสือเล่มนี้จาก Book Mark สำเร็จ');
                this.getBookmard();
            }
    }) 
  }

  data = [
    {
        name: 'John Brown',
    },
    {
        name: 'Jim Green',
    },
  ]

  columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
    },
    {
      title: 'Action',
      render: () => (
        <Button style = {{ width: '10vh'}} type="primary" onClick={() => this.onReset()} danger >
            Delete
        </Button>
      ),
    },
  ];

  render() {
    return (
      <Layout>
          <Table columns={this.columns} dataSource={this.data} />
      </Layout>
      
    );
  }
}
