import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import store from "store";
import Layout from '../Layout/Layout';
import { List, Card , Button, message} from 'antd';
export default class MyList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        data : []
    }
    this.getBookmard();
  }

  getBookmard = async() =>{
    var arr = [];
    const requestOptions = {
        method: 'GET',
        headers: { 
            'Content-Type': 'application/json',
        },
      };
      await fetch('/getBookmard?user='+store.get('token'), requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            myJson.forEach(element => {
                arr.push(element);
            });
            this.setState({data:arr});
        }) 
  }

  removeBookmark = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/removeBookmark?user='+store.get('token')+'&id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            if(myJson.status == 'Pass')
            {
                message.info('ลบหนังสือเล่มนี้จาก Book Mark สำเร็จ');
                this.getBookmard();
            }
    }) 
  }

  columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      render: text => <a>{text}</a>,
    },
    {
      title: 'Date',
      dataIndex: 'age',
      key: 'age',
    },
    {
      title: 'Address',
      dataIndex: 'address',
      key: 'address',
    },
    {
      title: 'Status',
      key: 'tags',
      dataIndex: 'tags',
      render: tags => (
        <>
          {tags.map(tag => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={color} key={tag}>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
  ];

  render() {
    return (
      <Layout>
          <Table columns={this.columns} dataSource={this.data} />
      </Layout>
      
    );
  }
}
