import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import store from "store";
import moment from 'moment';
import Layout from '../Layout/Layout';
import { Table, message , Button, Modal, DatePicker,Space} from 'antd';
export default class MyList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        data : [],
        visible : false,
        start : '',
        end : '',
    }
    this.getList();
  }

  getList = async() =>{
    var arr = [];
    const requestOptions = {
        method: 'GET',
        headers: { 
            'Content-Type': 'application/json',
        },
      };
      await fetch('/getList?user='+store.get('token'), requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            myJson.forEach(element => {
                arr.push(element);
            });
            this.setState({data:arr});
            console.log(this.state.data);
        }) 
  }

  removeList = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/removeList?user='+store.get('token')+'&id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            if(myJson.status == 'Pass')
            {
                message.info('ลบหนังสือเล่มนี้จากรายงานสำเร็จ');
                this.getList();
            }
    }) 
  }

  

  columns = [
    {
      title: 'Name',
      dataIndex: 'Name',
      key: 'Name',
    },
    {
        title: 'Author',
        dataIndex: 'Author',
        key: 'Author',
    },
    {
        title: 'Year',
        dataIndex: 'Year',
        key: 'Year',
    },
    {
      title: 'Action',
      render: (data) => (
        
        <Button style = {{ width: '10vh'}} type="primary" onClick={() => this.removeList(data.BookID)} danger >
            Delete
        </Button>
      ),
    },
  ];

  handleOk = (e) => {
    this.setState({
        visible: false
    });
  }

  handleCancel = (e) => {
    this.setState({
        visible: false
    });
  }


  onClick = () => {
    this.setState({
        visible: true
    });
  }

  onChangedate =  (date, dateString) =>  {
    this.setState({start:dateString});
    var result = new Date(dateString);
    result.setDate(result.getDate() + 7);
    this.setState({end:result.getFullYear()+'-'+parseInt(result.getMonth()+1)+'-'+result.getDate()});
  };

  render() {
    return (
        <Layout>
            <Table columns={this.columns} dataSource={this.state.data} />
            <div style={{ display: "flex",justifyContent:'flex-end' }}>
                <Button className="float-right" style = {{width: '30vh'}} type="primary" onClick={this.onClick}>
                    ดำเนินการ
                </Button>
            </div>  
            <Modal
                title="Confirm"
                visible={this.state.visible}
                onOk={this.handleOk}
                onCancel={this.handleCancel}
            >
                <p>วันที่คุณขอยืม</p>
                <Space direction="vertical">
                    <DatePicker onChange={this.onChangedate} />
                </Space>
                <p>วันที่คุณต้องคืน</p>
                <Space direction="vertical">
                    <DatePicker defaultValue={new Date(this.state.end)} disabled/>
                </Space>
            </Modal>
        </Layout>
      
    );
  }
}
