import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import store from "store";
import Layout from '../Layout/Layout';
import { List, Card , Button } from 'antd';
export default class Bookmark extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        data : []
    }
    this.getBookmard();
  }

  getBookmard = async() =>{
    var arr = [];
    const requestOptions = {
        method: 'GET',
        headers: { 
            'Content-Type': 'application/json',
        },
      };
      await fetch('/getBookmard?user='+store.get('token'), requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            myJson.forEach(element => {
                arr.push(element);
            });
            this.setState({data:arr});
        }) 
  }

  removeBookmark = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/removeBookmark?user='+store.get('token')+'&id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            myJson.forEach(element => {
                arr.push(element);
            });
        this.setState({data:arr});
    }) 
  }

  render() {
    return (
      <Layout>
        <List
            grid={{ gutter: 16, column: 4 }}
            dataSource={this.state.data}
            pagination={{
              onChange: page => {
                console.log(page);
              },
              pageSize: 8,
            }}
            renderItem={item => (
              <List.Item>
                <div>
                <Card hoverable style={{ width: 270 }} title={item.Name} cover={<img alt="example" src={item.url} />}>
                  <p>Author : {item.Author}</p>
                  <p>Year: {item.Year}</p>
                  <p>Stock : {item.stock}</p>
                  <div>
                  <Button style = {{width: 150}} type="primary" >
                    เพิ่มเข้ารายการ
                  </Button>
                  <br/><br/>
                  <Button style = {{width: 150}} type="primary" onClick={() => this.addBookmark(item.BookID)} danger>
                    ลบจาก Book Mark
                  </Button>
                </div>
                </Card>
                </div>
              </List.Item>
            )}
          />
      </Layout>
      
    );
  }
}
