import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import store from "store";
import Layout from '../Layout/Layout';
import { Table, Tag, Space } from 'antd';
export default class History extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data : []
    };
  }
  
  

  columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    render: text => <a>{text}</a>,
  },
  {
    title: 'วันที่ยืม',
    dataIndex: 'age',
    key: 'age',
  },
  {
    title: 'วันที่ต้องคืน',
    dataIndex: 'address',
    key: 'address',
  },
  {
    title: 'Status',
    key: 'tags',
    dataIndex: 'status',
    render: status => (
      <>
        {status.map(status => {
          let color ;
          let text ;
          if(status == 0)
          {
            color = 'volcano';
            text = 'ยังไม่มารับ';
          }
          if(status == 1)
          {
            color = 'blue';
            text = 'มารับแล้ว';
          }
          else
          {
            color = 'green';
            text = 'คืนแล้ว';
          }
          return (
            <Tag color={color} key={status}>
              {text.toUpperCase()}
            </Tag>
          );
        })}
      </>
    ),
  },
];

  render() {
    return (
      <Layout>
        <Table columns={this.columns} dataSource={this.data} />
      </Layout>
      
    );
  }
}
