import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import store from "store";
import Layout from '../Layout/Layout';
import { Table, message , Button, Modal, DatePicker,Space} from 'antd';
export default class MyList extends React.Component {

  constructor(props) {
    super(props);
    var startD = new Date().value;
    var endD = new Date().setDate(startD.getDate() + 7);
    this.state = {
        data : [],
        visible : false,
        start : '',
        end : '',
    }
    this.setState({start:startD.getFullYear()+'-'+parseInt(startD.getMonth()+1)+'-'+startD.getDate()});
    this.setState({end:endD.getFullYear()+'-'+parseInt(endD.getMonth()+1)+'-'+endD.getDate()});
    this.getList();
    
  }

  getList = async() =>{
    var arr = [];
    const requestOptions = {
        method: 'GET',
        headers: { 
            'Content-Type': 'application/json',
        },
      };
      await fetch('/getList?user='+store.get('token'), requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            myJson.forEach(element => {
                arr.push(element);
            });
            this.setState({data:arr});
            console.log(this.state.data);
        }) 
  }

  removeList = async(id) => {
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Content-Type': 'application/json',
        },
    };
    await fetch('/removeList?user='+store.get('token')+'&id='+id, requestOptions)
        .then(response=> response.json())
        .then(myJson=> {
            if(myJson.status == 'Pass')
            {
                message.info('ลบหนังสือเล่มนี้จากรายงานสำเร็จ');
                this.getList();
            }
    }) 
  }

  

  columns = [
    {
      title: 'Name',
      dataIndex: 'Name',
      key: 'Name',
    },
    {
        title: 'Author',
        dataIndex: 'Author',
        key: 'Author',
    },
    {
        title: 'Year',
        dataIndex: 'Year',
        key: 'Year',
    },
    {
      title: 'Action',
      render: (data) => (
        
        <Button style = {{ width: '10vh'}} type="primary" onClick={() => this.removeList(data.BookID)} danger >
            Delete
        </Button>
      ),
    },
  ];

  handleOk = (e) => {
    this.setState({
        visible: false
    });
  }

  handleCancel = (e) => {
    this.setState({
        visible: false
    });
  }


  onClick = () => {
    this.setState({
        visible: true
    });
  }
  render() {
    return (
        <Layout>
            <Table columns={this.columns} dataSource={this.state.data} />
            <div style={{ display: "flex",justifyContent:'flex-end' }}>
                <Button className="float-right" style = {{width: '30vh'}} type="primary" onClick={this.onClick}>
                    ดำเนินการ
                </Button>
            </div>  
            <Modal
                title="Confirm"
                visible={this.state.visible}
                onOk={this.handleOk}
                onCancel={this.handleCancel}
            >
                <p>วันที่คุณขอยืม    คือ {this.state.start}</p>
                <p>วันที่คุณต้องคืน   คือ {this.state.end}</p>

            </Modal>
        </Layout>
      
    );
  }
}
