import React, { useEffect, useState } from "react";
import { Layout, Menu, Icon, Badge } from "antd";
import { Link, withRouter } from "react-router-dom";
import { routes } from "../../router";

const { Content, Sider } = Layout;
const { SubMenu } = Menu;

const AppLayout = props => {
  const [collapsed, setCollapsed] = useState(false);
  const [notification, setNotification] = useState({});
  const auth = useRedirectAuth(props);
  if (auth) return auth;
  return (
    <Layout style={{ height: "100vh" }}>
      <Sider
        breakpoint="lg"
        collapsedWidth="0"
        onBreakpoint={broken => {
        }}
        onCollapse={(collapsed, type) => {
        }}
      >
        <div className="logo" style={{ padding: 10 }}>
          <img alt="" src={logo} style={{ width: 30, height: 30 }} />
          <span style={{ color: "white", fontSize: 14 }}> Sale steering</span>
        </div>
        <Menu
          theme="dark"
          mode="inline"
          defaultSelectedKeys={[props.location.pathname]}
          defaultOpenKeys={[`/${props.location.pathname.split("/")[1]}`]}
          style={{ textAlign: "left" }}
          inlineCollapsed={collapsed}
        >
          {routes.map(route =>
            route.nav ? (
              route.subPage ? (
                <SubMenu
                  key={route.path}
                  title={
                    <span>
                      <Badge count={notification[route.path]}>
                        <Icon type={route.icon} />
                      </Badge>
                      <span>{route.name}</span>
                    </span>
                  }
                >
                  {route.subPage.map(sub => (
                    <Menu.Item key={`${route.path}${sub.path}`}>
                      <Link to={`${route.path}${sub.path}`}>
                        <Badge count={notification[`${route.path}${sub.path}`]}>
                          <Icon type={sub.icon} />
                        </Badge>
                        <span className="nav-text">{sub.name}</span>
                      </Link>
                    </Menu.Item>
                  ))}
                </SubMenu>
              ) : (
                <Menu.Item key={route.path}>
                  <Link to={route.path}>
                    <Badge count={notification[route.path]}>
                      <Icon type={route.icon} />
                    </Badge>
                    <span className="nav-text">{route.name}</span>
                  </Link>
                </Menu.Item>
              )
            ) : null
          )}
        </Menu>
      </Sider>
      <Layout>
        <Content style={{ margin: "24px 16px 0" }}>
          <div style={{ padding: 24, background: "#fff", minHeight: "100vh" }}>
            {props.children}
          </div>
        </Content>
      </Layout>
    </Layout>
  );
};

export default withRouter(AppLayout);
