        year:'2542',
        url:'http://ku-press.ku.ac.th/newweb/images/bookimg/416/42-31_resize.jpg'
      },
      {
        title: 'Reading for Mass Communication',
        author: 'Asst.Prof.Bhikul Punyaratabandhu',
        year:'2542',
        url:'http://ku-press.ku.ac.th/newweb/images/bookimg/416/42-31_resize.jpg'
      },
    ];

    return (
      <Layout>
        <Search
          placeholder="input search text"
          style={{ width: 500 }}
          onSearch={value => console.log(value)}
        />
        <br/><br/><br/>
        <List
          grid={{ gutter: 16, column: 4 }}
          dataSource={data}
          pagination={{
            onChange: page => {
              console.log(page);
            },
            pageSize: 12,
          }}
          renderItem={item => (
            <List.Item>
              <div onClick={() => alert("Hello from here")}>
              <Card hoverable style={{ width: 300 }} title={item.title} cover={<img alt="example" src={item.url} />}>
                <p>Card content</p>
                <p>Card content</p>
                <p>Card content</p>
