import React from 'react';
import 'antd/dist/antd.css';
import './custom.css'
import { List, Card , Button, Input } from 'antd';
import store from "store";
import Layout from '../Layout/Layout';
export default class Homepage extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      text : '',
      data : []
    };
    this.getAllbook();
    
  }

  getAllbook = async() => {
    const requestOptions = {
      method: 'GET',
      headers: { 
          'Content-Type': 'application/json',
      },
    };
    await fetch('/allbook', requestOptions)
      .then(response=> response.json())
      .then(myJson=> {
        myJson.forEach(element => {
          console.log(element);
          this.state.data.push(element);
        });
      }) 
  };

  formRef = React.createRef();

   layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };

  tailLayout = {
    wrapperCol: {
      offset: 5,
      span: 16,
    },
  };

  tailLayout1 = {
    wrapperCol: {
      offset: 6,
      span: 16,
    },
  };
  data = [];
  render() {
    var Search = Input.Search;
    

    return (
      <Layout>
        <Search
          placeholder="input search text"
          style={{ width: 500 }}
          onSearch={value => console.log(value)}
        />
        <br/><br/><br/>
        {
          this.state.data.length==0 ? 
          <label style={{ color: 'BLACK',fontWeight:'bold',fontSize:36}}>ไม่มีหนังสือที่คุณค้นหา</label>
           : 
          
            <List
            grid={{ gutter: 16, column: 4 }}
            dataSource={this.state.data}
            pagination={{
              onChange: page => {
                console.log(page);
              },
              pageSize: 12,
            }}
            renderItem={item => (
              <List.Item>
                <div onClick={() => alert("Hello from here")}>
                <Card hoverable style={{ width: 300 }} title={item.title} cover={<img alt="example" src={item.url} />}>
                  <p>Author : {item.author}</p>
                  <p>Year: {item.year}</p>
                </Card>
                </div>
              </List.Item>
            )}
          />
          
          
        }
      </Layout>
      
    );
  }
}
